import math

from InvSolver.old_solver.Setting import desc_size_globle


def Desc_Text_alignment_tools(text, fill_char=chr(12288), max_size=desc_size_globle):
    """
    将text对齐,多余的截断不够的填充
    :param fill_char: 用以填充的字符,def为中文空格 chr(12288)
    :param text: 要显示的文字
    :param max_size: 总长度
    :return: 经过对其格式化后的结果
    """
    text = text[:desc_size_globle]
    return str(text + fill_char * (desc_size_globle - len(text)))

import numpy as np
import numpy.linalg

class Points_In_Coners_Tools:
    def __init__(self,coners:list):
        """
        注意coners中不能有0,可以是非凸的
        :param coners: coners是矩形框的8个角的坐标,点的顺序转换成：后左上，后右上，后右下，后左下，前左上，前右上，前右下，前左下

        """
        p = np.array(coners)
        result = np.array([[1], [1], [1], [1]])

        coefficient = p[:4,]#后面
        self.ABC1 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        coefficient = p[4:,]#前面
        self.ABC2 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        #判断法线是否同向，内积与向量符号一致
        dot_product = np.inner(self.ABC1.reshape(3), self.ABC2.reshape(3))
        # dot_product = np.inner(ABC1.reshape(3), ABC2.reshape(3))/numpy.linalg.norm(ABC1.reshape(3))/numpy.linalg.norm(ABC2.reshape(3))
        # angle = np.arccos(dot_product)/np.pi*180
        if (dot_product < 0):
            self.ABC1 = -self.ABC1

        coefficient = p[[0,3,4,7],]#左面
        self.ABC3 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        coefficient = p[[1,2,5,6],]#右面
        self.ABC4 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        dot_product = np.inner(self.ABC3.reshape(3), self.ABC4.reshape(3))
        if (dot_product < 0):
            self.ABC3 = -self.ABC3

        coefficient = p[[0,1,4,5],]#上面
        self.ABC5 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        coefficient = p[[2,3,6,7],]#下面
        self.ABC6 = np.dot(np.dot(np.linalg.pinv(np.dot(coefficient.T, coefficient)), coefficient.T), result)
        dot_product = np.inner(self.ABC5.reshape(3), self.ABC6.reshape(3))
        if (dot_product < 0):
            self.ABC5 = -self.ABC5
        #p1，p2作为法线的基点
        self.p1 = p[0,]
        self.p2 = p[6,]
    def is_in(self,points)->list:
        """
        判断这些点是不是在立方体内
        :param points: 带判断点的坐标
        :return:[是否在,[x,y,z]]
        """
        point_detected = []
        # for point in tqdm(points,desc=Desc_Text_alignment_tools("正在判断点在不在区域内")):
        for point in points:
            p1p = self.p1 - point
            p2p = self.p2 - point
            direct1 = np.inner(self.ABC1.reshape(3), p1p.reshape(3))
            direct2 = np.inner(self.ABC2.reshape(3), p2p.reshape(3))
            if (direct1 > 0 and direct2 < 0) or (direct1 < 0 and direct2 > 0):
                direct1 = np.inner(self.ABC3.reshape(3), p1p.reshape(3))
                direct2 = np.inner(self.ABC4.reshape(3), p2p.reshape(3))
                if (direct1 > 0 and direct2 < 0) or (direct1 < 0 and direct2 > 0):
                    direct1 = np.inner(self.ABC5.reshape(3), p1p.reshape(3))
                    direct2 = np.inner(self.ABC6.reshape(3), p2p.reshape(3))
                    if (direct1 > 0 and direct2 < 0) or (direct1 < 0 and direct2 > 0):
                        point_detected.append([True,point])
                    else:
                        point_detected.append([False, point])
                else:
                    point_detected.append([False, point])
            else:
                point_detected.append([False, point])
        return point_detected


# 计算平均值
def mean(x):
    return sum(x) / len(x)


# 计算每一项数据与均值的差
def de_mean(x):
    x_bar = mean(x)
    return [x_i - x_bar for x_i in x]


# 辅助计算函数 dot product 、sum_of_squares
def dot(v, w):
    return sum(v_i * w_i for v_i, w_i in zip(v, w))


def sum_of_squares(v):
    return dot(v, v)


# 方差
def variance(x):
    n = len(x)
    deviations = de_mean(x)
    return sum_of_squares(deviations) / (n - 1)


# 标准差
def standard_deviation(x):
    return math.sqrt(variance(x))


# 协方差
def covariance(x, y):
    n = len(x)
    return dot(de_mean(x), de_mean(y)) / (n - 1)


# 相关系数
def correlation(x, y):
    stdev_x = standard_deviation(x)
    stdev_y = standard_deviation(y)
    if stdev_x > 0 and stdev_y > 0:
        return covariance(x, y) / stdev_x / stdev_y
    else:
        return 0


